import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Masonry from 'react-masonry-css';
import './App.css';
import ImageWithCaption from './ImageWithCaption'; 
import NavBar from './NavBar';
import About from './About';
import Overlay from './Overlay';
import FixedImage from './FixedImage';
import FixedImage2 from './FixedImage2';
import shuffleArray from './shuffleArray';

const imagesData = [
  {src: "/img/new_isc.png",
  title: 'new_ISC="ITCT(20th)"',
  year: "2022",
  tags: ["identity", "graphic"],
  id: "1",
  video: "",
  description: (
    <>
      정보문화학 과제전입니다.
      <br />
      다음 줄에 텍스트를 표시합니다.
    </>
  ),
  additionalImages: [
    "/img/new_isc_1.png",
    "/img/new_isc_2.png",
    "/img/new_isc_3.png",
    "/img/new_isc_4.png",
    "/img/new_isc_5.png",
    "/img/new_isc_6.png",
    "/img/new_isc_7.png",
  ]},

  {src: "/img/makecornpop.gif",
  title: "Make Corn Pop!",
  year: "2022",
  tags: ["website"],
  id: "2",
  video: "n5If5jaoq5s",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/getrelaxed.gif",
  title: "Refresh and Get Your Own Landscape",
  year: "2022",
  tags: ["website"],
  id: "3",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/fcm.gif",
  title: "Fridge Cliche Mart",
  year: "2023",
  tags: ["graphic", "website"],
  id: "4",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/finilsan.gif",
  title: "일산: 인 픽션",
  year: "2021",
  tags: ["graphic", "motion"],
  id: "5",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/isc_website.gif",
  title: "정보문화학과 홈페이지",
  year: "2022",
  tags: ["website"],
  id: "6",
  video: "n4YX-Qpb8LU",
  description: "정보문화학과 홈페이지입니다.",
  additionalImages: [
  ]},

  {src: "/img/planner.png",
  title: "루틴 플래너",
  year: "2022",
  tags: ["editorial"],
  id: "7",
  video: "",
  description: "플래너.",
  additionalImages: [
    "/img/planner_1.png",
    "/img/planner_2.png",
    "/img/planner_3.png",
    "/img/planner_4.png",
    "/img/planner_5.png",
  ]},

  {src: "/img/iloveyoumelisa.gif",
  title: "사랑해, 멜리사",
  year: "2021",
  tags: ["motion"],
  id: "8",
  video: "",
  description: "멜리사가 사랑한대",
  additionalImages: [
  ]},

  {src: "/img/whatsinmybag.gif",
  title: "What's in Hyezzz's Bag?",
  year: "2022",
  tags: ["website"],
  id: "9",
  video: "",
  description: (
    <>
      혜원이의 가방에는 무엇이 들어있을까요?
      <br />
      https://hy3zzz.github.io/What-s-in-Hyezzz-s-Bag/
    </>
  ),
  additionalImages: [
  ]},

  {src: "/img/gyeongui.png",
  title: "경의중앙선에서 마주치다",
  year: "2021",
  tags: ["editorial"],
  id: "10",
  video: "",
  description: "경의중앙선 배차 간격 좀 제발 늘려줘",
  additionalImages: [
  ]},
];

function App() {
  const allTags = [...new Set(imagesData.flatMap(image => image.tags))];
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleOverlayClose = () => {
    setSelectedImage(null);
  };

  const [selectedTag, setSelectedTag] = useState(null);

  const handleTagClick = (tag) => {
    setSelectedTag(tag);
  };

  const [images, setImages] = useState(imagesData);
  const [filteredImages, setFilteredImages] = useState([]);

  // shuffle images only once when the component is mounted
  useEffect(() => {
    const shuffledImages = shuffleArray([...images]); 
    setImages(shuffledImages);
  }, []);

  // filter shuffled images whenever selected tag changes
  useEffect(() => {
    const newFilteredImages = selectedTag
      ? images.filter((image) => image.tags.includes(selectedTag))
      : images;

    setFilteredImages(newFilteredImages);
  }, [selectedTag, images]);

  const Gallery = () => (
    <Masonry
      breakpointCols={{
        default: 3,
        1100: 3,
        700: 2,
        500: 1
      }}
      className="masonry-grid"
      columnClassName="masonry-grid_column"
    >
      {filteredImages.map((image) => (
        <ImageWithCaption
          key={image.id}
          src={image.src}
          title={image.title}
          year={image.year}
          tags={image.tags}
          onTagClick={handleTagClick}
          onClick={() => handleImageClick(image)}
        />
      ))}
    </Masonry>
  );

return (
  <Router>
    <NavBar
      allTags={allTags}
      selectedTag={selectedTag}
      onTagClick={handleTagClick} />
    <Routes>
      <Route path="/" element={<Gallery />} />
      <Route path="/about" element={<About />} />
    </Routes>
    {selectedImage && (
      <Overlay
        image={selectedImage}
        onClose={handleOverlayClose}
      />
    )}
    <FixedImage src="/img/bubble.png" />
    <FixedImage2 src="/img/bubble2.png" />
  </Router>
);
}

export default App;
