import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Masonry from 'react-masonry-css';
import './App.css';
import ImageWithCaption from './ImageWithCaption'; 
import NavBar from './NavBar';
import About from './About';
import Overlay from './Overlay';

const imagesData = [
  {src: "/img/new_isc.png",
  title: 'new_ISC="ITCT(20th)"',
  year: "2022",
  tags: ["identity"],
  id: "1",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
    "/img/new_isc_1.png",
    "/img/new_isc_2.png",
    "/img/new_isc_3.png",
    "/img/new_isc_4.png",
    "/img/new_isc_5.png",
    "/img/new_isc_6.png",
    "/img/new_isc_7 d.png",
  ]},

  {src: "/img/makecornpop.gif",
  title: "Make Corn Pop!",
  year: "2022",
  tags: ["website"],
  id: "2",
  video: "n5If5jaoq5s",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/getrelaxed.gif",
  title: "Refresh and Get Your Own Landscape",
  year: "2022",
  tags: ["website"],
  id: "3",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/fcm.gif",
  title: "Fridge Cliche Mart",
  year: "2023",
  tags: ["graphic", "website"],
  id: "4",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/finilsan.gif",
  title: "일산: 인 픽션",
  year: "2021",
  tags: ["graphic", "motion"],
  id: "5",
  video: "",
  description: "정보문화학 과제전입니다.",
  additionalImages: [
  ]},

  {src: "/img/isc_website.gif",
  title: "정보문화학과 홈페이지",
  year: "2022",
  tags: ["website"],
  id: "6",
  video: "n4YX-Qpb8LU",
  description: "정보문화학과 홈페이지입니다.",
  additionalImages: [
  ]},

  {src: "/img/planner.png",
  title: "루틴 플래너",
  year: "2022",
  tags: ["editorial"],
  id: "6",
  video: "",
  description: "플래너.",
  additionalImages: [
    "/img/planner_1.png",
    "/img/planner_2.png",
    "/img/planner_3.png",
    "/img/planner_4.png",
    "/img/planner_5.png",
  ]},
];

function FixedImage({ src, alt }) {
  return (
    <img
      src={src}
      alt={alt}
      style={{
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        width: '100px',  // adjust size as necessary
      }}
    />
  );
}

function App() {
  const allTags = [...new Set(imagesData.flatMap(image => image.tags))];

  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleOverlayClose = () => {
    setSelectedImage(null);
  };

  const [selectedTag, setSelectedTag] = useState(null);

  const handleTagClick = (tag) => {
    setSelectedTag(tag);
  };

  const filteredImages = selectedTag
    ? imagesData.filter((image) => image.tags.includes(selectedTag))
    : imagesData;

  const Gallery = () => (
    <Masonry
      breakpointCols={{
        default: 3,
        1100: 3,
        700: 2,
        500: 1
      }}
      className="masonry-grid"
      columnClassName="masonry-grid_column"
    >
      {filteredImages.map((image) => (
        <ImageWithCaption
          key={image.id}
          src={image.src}
          title={image.title}
          year={image.year}
          tags={image.tags}
          onTagClick={handleTagClick}
          onClick={() => handleImageClick(image)}
        />
      ))}
    </Masonry>
  );

return (
  <Router>
    <NavBar allTags={allTags} selectedTag={selectedTag} onTagClick={handleTagClick} />
    <Routes>
      <Route path="/" element={<Gallery />} />
      <Route path="/about" element={<About />} />
    </Routes>
    {selectedImage && (
      <Overlay
        image={selectedImage}
        onClose={handleOverlayClose}
      />
    )}
    <FixedImage src="/img/bubble.png" />
  </Router>
);
}

export default App;
