import React, {useState} from 'react';
import Caption from './Caption';
import './Caption.css';

function ImageWithCaption({ src, title, year, tags, onTagClick, isHovered, onMouseEnter, onMouseLeave }) {
  const [captionPosition, setCaptionPosition] = useState(null);

  const handleMouseEnter = () => {
    const positions = ["top left", "top right", "bottom left", "bottom right"];
    const randomPosition = positions[Math.floor(Math.random() * positions.length)];
    setCaptionPosition(randomPosition);
    onMouseEnter();
  };

  const handleMouseLeave = () => {
    setCaptionPosition(null);
    onMouseLeave();
  };

  return (
    <div 
      style={{ position: "relative", padding: "20px" }} 
      onMouseEnter={handleMouseEnter} 
      onMouseLeave={handleMouseLeave}
    >
      <img src={src} style={{ width: "100%", height: "100%" }} />
      {isHovered && (
        <div style={{ position: "absolute", [captionPosition.split(" ")[0]]: "0", [captionPosition.split(" ")[1]]: "0", zIndex: "1" }}>
          <Caption title={title} year={year} tags={tags} isHovered={isHovered} onTagClick={onTagClick}/>
        </div>
      )}
    </div>
  );
}

export default ImageWithCaption;

