import React, { useState } from 'react';
import Masonry from 'react-masonry-css';
import './App.css';

const imagesData = [
  {src: "/img/new_isc.png", title: "정보문화학과 20주년 과제전", project: "A"},
  {src: "/img/makecornpop.gif", title: "Make Corn Pop!", project: "B"},
  {src: "/img/getrelaxed.gif", title: "Get Relaxed", project: "B"}
];

function App() {
  const [images] = useState(imagesData);

  const ImageItem = ({ item }) => (
    <div className="image-item">
      <img
        className={item.project === 'A' ? 'project-a' : 'project-b'}
        src={item.src}
        alt={item.title}
        onMouseOver={e => e.currentTarget.alt = item.title}
        onMouseOut={e => e.currentTarget.alt = ''}
      />
      <div className="image-caption">{item.title}</div>
    </div>
  );

  const breakpointColumnsObj = {
    default: 6,
    1100: 6,
    700: 3,
    500: 2
  };

  return (
    <div className="App">
      <Masonry
        breakpointCols={breakpointColumnsObj}
        className="masonry-grid"
        columnClassName="masonry-grid_column"
      >
        {images.map(item => (
          <ImageItem item={item} key={item.src} />
        ))}
      </Masonry>
    </div>
  );
}

export default App;
