import React from 'react';

function Caption({ title, year, tags, onTagClick }) {
    return (
      <div className="caption">
        <h2>{title}</h2>
        <p>{year}</p>
        <div className="tags">
          {tags.map((tag, index) => (
            <span key={index} className="tag" onClick={() => onTagClick(tag)}>
              {tag}
            </span>
          ))}
        </div>
      </div>
    );
}

export default Caption;
