import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Masonry from 'react-masonry-css';
import './App.css';
import ImageWithCaption from './ImageWithCaption'; 
import NavBar from './NavBar';
import About from './About';

const imagesData = [
  {src: "/img/new_isc.png",
  title: 'new_ISC="ITCT(20th)"',
  year: "2022",
  tags: ["identity"],
  id: "1"},

  {src: "/img/makecornpop.gif",
  title: "Make Corn Pop!",
  year: "2022",
  tags: ["website"],
  id: "2"},

  {src: "/img/getrelaxed.gif",
  title: "Refresh and Get Your Own Landscape",
  year: "2022",
  tags: ["website"],
  id: "3"},

  {src: "/img/fcm.gif",
  title: "Fridge Cliche Mart",
  year: "2023",
  tags: ["graphic", "website"],
  id: "4"},

  {src: "/img/finilsan.gif",
  title: "일산: 인 픽션",
  year: "2021",
  tags: ["graphic", "motion"],
  id: "4"},
];

function App() {
  const [images] = useState(imagesData);
  const [isHovered, setIsHovered] = useState({});

  const handleMouseEnter = (id) => {
    setIsHovered(prevState => ({...prevState, [id]: true}));
  };

  const handleMouseLeave = (id) => {
    setIsHovered(prevState => ({...prevState, [id]: false}));
  };  

  const handleToggleHover = () => {
    if (Object.values(isHovered).some(hoverState => hoverState)) {
      // If any image is hovered, un-hover all
      setIsHovered({});
    } else {
      // Otherwise, hover all
      const newHoverState = {};
      images.forEach(image => {
        newHoverState[image.id] = true;
      });
      setIsHovered(newHoverState);
    }
  };  

  const [selectedTag, setSelectedTag] = useState(null);
  const handleTagClick = (tag) => {
    setSelectedTag(tag);
  };

  const filteredImages = selectedTag
  ? images.filter((image) => image.tags.includes(selectedTag))
  : images;

  const breakpointColumnsObj = {
    default: 3,
    1100: 3,
    700: 2,
    500: 1
  };

  const Gallery = () => (
    <Masonry
      breakpointCols={breakpointColumnsObj}
      className="masonry-grid"
      columnClassName="masonry-grid_column"
    >
      {filteredImages.map(({ src, alt, title, year, tags, id }) => (
        <ImageWithCaption 
          key={id}
          src={src}
          alt={alt}
          title={title}
          year={year}
          tags={tags}
          isHovered={isHovered}
          onTagClick={handleTagClick}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        />
      ))}
    </Masonry>
  );

  return (
    <Router>
      <NavBar onToggleHover={handleToggleHover} isHovered={isHovered} />
      <Routes>
        <Route path="/about" element={<About />} />
        <Route path="/" element={<Gallery />} />
      </Routes>
    </Router>
  );
};

export default App;
