import React, { useState } from 'react';
import Masonry from 'react-masonry-css';
import './App.css';
import Caption from "./Caption";

const imagesData = [
  {src: "/img/new_isc.png",
  title: "정보문화학과 20주년 홈커밍 & 과제전",
  year: "2022",
  tags: ["identity"],
  id: "1"},

  {src: "/img/makecornpop.gif",
  title: "Make Corn Pop!",
  year: "2022",
  tags: ["website"],
  id: "2"},

  {src: "/img/getrelaxed.gif",
  title: "Refresh and Get Your Own Landscape",
  year: "2022",
  tags: ["website"],
  id: "3"},
];

function App() {
  const [images] = useState(imagesData);

  const breakpointColumnsObj = {
    default: 3,
    1100: 3,
    700: 2,
    500: 1
  };

  return (
    <Masonry
      breakpointCols={breakpointColumnsObj}
      className="masonry-grid"
      columnClassName="masonry-grid_column"
    >
    {images.map(({ src, alt, title, year, tags, id }) => {
      const [isHovered, setIsHovered] = useState(false);
      return (
        <div 
          key={id} 
          onMouseEnter={() => setIsHovered(true)} 
          onMouseLeave={() => setIsHovered(false)}
        >
          <img src={src} alt={alt} />
          {isHovered && <Caption title={title} year={year} tags={tags} />}
        </div>
      );
    })}
  </Masonry>
  );
};

export default App;
