import React from 'react';

function Overlay({ src, title, year, tags, onCloseOverlay }) {
  return (
    <div className="overlay">
      <div className="overlay-content">
        <img src={src} alt={title} className="overlay-image" />
        <div className="overlay-info">
          <h3 className="overlay-title">{title}</h3>
          <p className="overlay-year">{year}</p>
          <div className="overlay-tags">
            {tags.map((tag, index) => (
              <span key={index} className="overlay-tag">
                {tag}
              </span>
            ))}
          </div>
        </div>
        <button className="overlay-close" onClick={onCloseOverlay}>
          Close
        </button>
      </div>
    </div>
  );
}

export default Overlay;
