import React from 'react';
import { CSSTransition } from 'react-transition-group';
import './Caption.css';

function Caption({ title, year, tags, isHovered, onTagClick }) {
  return (
    <CSSTransition
      in={isHovered}
      timeout={300}
      classNames="caption"
      unmountOnExit
    >
      <div>
        <h3 className="caption-title">{title}</h3>
        <p className="caption-year">{year}</p>
        <div className="tags">
          {tags.map((tag, index) => (
            <span key={index} className="caption-tag" onClick={() => onTagClick(tag)}>
              {tag}
            </span>
          ))}
        </div>
      </div>
    </CSSTransition>
  );
}

export default Caption;
