import React, { useState } from 'react';
import Masonry from 'react-masonry-css';
import './App.css';
import Caption from "./Caption";

const imagesData = [
  {src: "/img/new_isc.png", alt: "정보문화학과 20주년 과제전", caption: "훌쩍", id: "1"},
  {src: "/img/makecornpop.gif", alt: "Make Corn Pop!", id: "2"},
  {src: "/img/getrelaxed.gif", alt: "Get Relaxed", id: "3"}
];

function App() {
  const [images] = useState(imagesData);

  const ImageItem = ({ item }) => (
    <div className="image-item">
      <img
        src={item.src}
        alt={item.title}
        onMouseOver={e => e.currentTarget.alt = item.title}
        onMouseOut={e => e.currentTarget.alt = ''}
      />
      <div className="image-caption">{item.title}</div>
    </div>
  );

  const breakpointColumnsObj = {
    default: 3,
    1100: 3,
    700: 2,
    500: 1
  };

  return (
    <Masonry
      breakpointCols={breakpointColumnsObj}
      className="masonry-grid"
      columnClassName="masonry-grid_column"
    >
      {images.map(({ src, alt, caption, id }) => (
        <div key={id}>
          <img src={src} alt={alt} />
          <div className="caption">{caption}</div>
        </div>
      ))}
    </Masonry>
  );
};

export default App;
