import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useHistory } from 'react-router-dom';
import Masonry from 'react-masonry-css';
import './App.css';
import ImageWithCaption from './ImageWithCaption'; 
import NavBar from './NavBar';
import About from './About';
import Overlay from './Overlay';

const imagesData = [
  {src: "/img/new_isc.png",
  title: 'new_ISC="ITCT(20th)"',
  year: "2022",
  tags: ["identity"],
  id: "1",
  additionalImages: [
  ]},

  {src: "/img/makecornpop.gif",
  title: "Make Corn Pop!",
  year: "2022",
  tags: ["website"],
  id: "2"},

  {src: "/img/getrelaxed.gif",
  title: "Refresh and Get Your Own Landscape",
  year: "2022",
  tags: ["website"],
  id: "3"},

  {src: "/img/fcm.gif",
  title: "Fridge Cliche Mart",
  year: "2023",
  tags: ["graphic", "website"],
  id: "4"},

  {src: "/img/finilsan.gif",
  title: "일산: 인 픽션",
  year: "2021",
  tags: ["graphic", "motion"],
  id: "4"},
];

function App() {
  const history = useHistory();
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (image) => {
    setSelectedImage(image);
    history('/overlay');
  };

  const handleOverlayClose = () => {
    setSelectedImage(null);
    history('/');
  };

  const [selectedTag, setSelectedTag] = useState(null);

  const handleTagClick = (tag) => {
    setSelectedTag(tag);
  };

  const filteredImages = selectedTag
    ? imagesData.filter((image) => image.tags.includes(selectedTag))
    : imagesData;

  const Gallery = () => (
    <Masonry
    breakpointCols={{
      default: 3,
      1100: 3,
      700: 2,
      500: 1
    }}
    className="masonry-grid"
    columnClassName="masonry-grid_column"
  >
    {filteredImages.map(({ src, title, year, tags, id }) => (
      <ImageWithCaption
        key={id}
        src={src}
        title={title}
        year={year}
        tags={tags}
        onTagClick={() => handleTagClick(tags)}
        onClick={() => handleImageClick({ src, title, year, tags })}
      />
    ))}
  </Masonry>
);

  return (
    <Router>
      <NavBar />
      <Routes>
        <Route path="/" element={<Gallery />} />
        {selectedImage && (
          <Route
            path="/overlay"
            element={
              <Overlay
                src={selectedImage.src}
                title={selectedImage.title}
                year={selectedImage.year}
                tags={selectedImage.tags}
                onClose={handleOverlayClose}
              />
            }
          />
        )}
        <Route path="/about" element={<About />} />
      </Routes>
    </Router>
  );
}

export default App;
