import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Masonry from 'react-masonry-css';
import './App.css';
import ImageWithCaption from './ImageWithCaption'; 
import NavBar from './NavBar';
import About from './About';

const imagesData = [
  {src: "/img/new_isc.png",
  title: 'new_ISC="ITCT(20th)"',
  year: "2022",
  tags: ["identity"],
  id: "1"},

  {src: "/img/makecornpop.gif",
  title: "Make Corn Pop!",
  year: "2022",
  tags: ["website"],
  id: "2"},

  {src: "/img/getrelaxed.gif",
  title: "Refresh and Get Your Own Landscape",
  year: "2022",
  tags: ["website"],
  id: "3"},

  {src: "/img/fcm.gif",
  title: "Fridge Cliche Mart",
  year: "2023",
  tags: ["graphic", "website"],
  id: "4"},

  {src: "/img/finilsan.gif",
  title: "일산: 인 픽션",
  year: "2021",
  tags: ["graphic", "motion"],
  id: "4"},
];

function App() {
  const [images] = useState(imagesData);
  const [hoverState, setHoverState] = useState(Array(imagesData.length).fill(false));
  const [allHovered, setAllHovered] = useState(false);

  const [selectedTag, setSelectedTag] = useState(null);

  const handleTagClick = (tag) => {
    setSelectedTag(tag);
  };

  const filteredImages = selectedTag
    ? images.filter((image) => image.tags.includes(selectedTag))
    : images;

  const breakpointColumnsObj = {
    default: 3,
    1100: 3,
    700: 2,
    500: 1
  };

  const handleMouseEnter = (index) => {
    const newHoverState = [...hoverState];
    newHoverState[index] = true;
    setHoverState(newHoverState);
  };

  const handleMouseLeave = (index) => {
    const newHoverState = [...hoverState];
    newHoverState[index] = false;
    setHoverState(newHoverState);
  };

  const handleToggleHover = () => {
    const newHoverState = hoverState.map((hovered) => !allHovered);
    setHoverState(newHoverState);
    setAllHovered(!allHovered);  // allHovered 상태도 업데이트
  };

  const Gallery = () => (
    <Masonry
      breakpointCols={breakpointColumnsObj}
      className="masonry-grid"
      columnClassName="masonry-grid_column"
    >
      {filteredImages.map(({ src, alt, title, year, tags, id }, index) => (
        <ImageWithCaption 
          key={id}
          src={src}
          alt={alt}
          title={title}
          year={year}
          tags={tags}
          onTagClick={handleTagClick}
          isHovered={hoverState[index]}
          onMouseEnter={() => handleMouseEnter(index)}
          onMouseLeave={() => handleMouseLeave(index)}
        />
      ))}
    </Masonry>
  );

  return (
    <Router>
      <NavBar onClick={handleToggleHover} allHovered={allHovered} />
      <Routes>
        <Route path="/about" element={<About />} />
        <Route path="/" element={<Gallery />} />
      </Routes>
    </Router>
  );
};

export default App;
