import React from 'react';
import Caption from './Caption';

const captionPositions = ["top-left", "top-right", "bottom-left", "bottom-right"];

function getRandomCaptionPosition() {
  const randomIndex = Math.floor(Math.random() * captionPositions.length);
  return captionPositions[randomIndex];
}


function ImageWithCaption({ src, title, year, tags, onTagClick, isHovered }) {
  return (
    <div 
      style={{ position: "relative", padding: "15px" }} 
    >
      <img src={src} style={{ width: "100%", height: "100%" }} />
      {isHovered && (
          <div style={{ position: "absolute", [captionPosition]: "0", zIndex: "1" }}>
          <Caption title={title} year={year} tags={tags} isHovered={isHovered} onTagClick={onTagClick}/>
        </div>
      )}
    </div>
  );
}

export default ImageWithCaption;
