import React from 'react';
import './Caption.css';

function Caption({ title, year, tags, isHovered, onTagClick }) {
  return (
    <div className={`caption-content ${isHovered ? 'show' : ''}`}>
      <div className="caption">
        <h3 className="caption-title">{title}</h3>
        <p className="caption-year">{year}</p>
        <div className="tags">
          {tags.map((tag, index) => (
            <span key={index} className="tag" onClick={() => onTagClick(tag)}>
              {tag}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Caption;
