import React from 'react';
import './Overlay.css';
import { tagColors } from './tagColors';

function Overlay({ image, onClose }) {
  if (!image) {
    return null;
  }

  return (
    <div className="overlay" onClick={onClose}>
      <div className="overlay-content" onClick={event => event.stopPropagation()}>
        <button className="overlay-close-button" onClick={onClose}>닫기</button>
        <div className="overlay-content-wrapper">
            <div className="overlay-text">
            <h2>{image.title}</h2>
            <p>{image.year}</p>
            {image.tags && image.tags.map((tag, index) => (
              <span
                key={index}
                className='overlay-tag'
                style={{ backgroundColor: tagColors[tag] }}
              >
                {tag}
              </span>
            ))}
            <p>{image.description}</p>
            </div>

            <div className="overlay-image">
                <img src={image.src} alt={image.title} />
                {image.video && (
                    <iframe
                        width="560"
                        height="315"
                        src={`https://www.youtube.com/embed/${image.video}`}
                        title="YouTube video player"
                        frameborder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        allowfullscreen
                    >
                    </iframe>
                )}
                {image.additionalImages.map((additionalImage, index) => (
                <img key={index} src={additionalImage} alt={`Additional Image ${index + 1}`} />
                ))}
            </div>
        </div>
      </div>
    </div>
  );
}

export default Overlay;
