import React from 'react';
import { useSpring, animated } from 'react-spring';
import './Caption.css';

function Caption({ title, year, tags, isHovered, onTagClick }) {
  const style = useSpring({
    opacity: isHovered ? 1 : 0,
  });

  return (
    <animated.div style={style} className="caption">
      <h3 className="caption-title">{title}</h3>
      <p className="caption-year">{year}</p>
      <div className="tags">
        {tags.map((tag, index) => (
          <span key={index} className="caption-tag" onClick={() => onTagClick(tag)}>
            {tag}
          </span>
        ))}
      </div>
    </animated.div>
  );
}

export default Caption;
